const addForm=document.querySelector(".add"),list=document.querySelector(".list"),search=document.querySelector(".search input"),createTask=e=>{const t=`\n    <li class="list__item">\n      <span>${e}</span><i class="delete fas fa-trash"></i>\n    </li>\n  `;list.innerHTML+=t};addForm.addEventListener("submit",e=>{e.preventDefault();const t=addForm.add.value.trim();t.length&&(createTask(t),addForm.reset())}),list.addEventListener("click",e=>{e.target.classList.contains("delete")&&e.target.parentElement.remove()});const filterTasks=e=>{Array.from(list.children).filter(t=>!t.textContent.toLowerCase().includes(e).forEach(e=>e.classList.add("filtered"))),Array.from(list.children).filter(t=>t.textContent.toLowerCase().includes(e).forEach(e=>e.classList.remove("filtered")))};search.addEventListener("keyup",()=>{const e=search.value.trim().toLowerCase();filterTasks(e)});